  const styles = [
    "Vintage",
    "Minimal",
    "Casual",
    "Business Casual",
    "Business",
    "Street",
    "Classic",
  ];

  const toggleStyle = (style) => {
    setSelectedStyles((prevSelectedStyles) =>
      prevSelectedStyles.includes(style)
        ? prevSelectedStyles.filter((s) => s !== style)
        : [...prevSelectedStyles, style]
    );
  };

  const handleSignup = (e) => {
